      var cta_primary_header_fontColor = values.cta_primary_header_fontColor;
      var cta_primary_header_bgColor = values.cta_primary_header_bgColor;

      var categoria;
      if(values.tema_categoria == null || values.tema_categoria == ""){
        categoria = 1
      }else{
        categoria = values.tema_categoria
      }

      axios.post(`${URL}/theme`, {"name": values.nome_tema, "description": values.descricao_tema, "category_id": categoria})
      .then((res) => {
        axios.post(`${URL}/theme/` + res.data.id + `/config`, {"theme_ss_id": 1, "theme_feature_id": 1, "value": titulo_header_bold})
        .then((res) => {
          axios.post(`${URL}/theme/` + res.data.id + `/config`, {"theme_ss_id": 1, "theme_feature_id": 2, "value": titulo_header_itallic})
          .then((res) => {
            axios.post(`${URL}/theme/` + res.data.id + `/config`, {"theme_ss_id": 1, "theme_feature_id": 3, "value": titulo_header_underline})
            .then((res) => {
              axios.post(`${URL}/theme/` + res.data.id + `/config`, {"theme_ss_id": 1, "theme_feature_id": 4, "value": titulo_header_align})
              .then((response) => {
                axios.post(`${URL}/theme/` + res.data.id + `/config`, {"theme_ss_id": 1, "theme_feature_id": 5, "value": titulo_header_fontStyle})
