                                            .then((res) => {
                                              axios.post(`${URL}/theme/` + res.data.id + `/config`, {"theme_ss_id": 3, "theme_feature_id": 4, "value": cta_primary_header_align})
                                              .then((res) => {
                                                axios.post(`${URL}/theme/` + res.data.id + `/config`, {"theme_ss_id": 3, "theme_feature_id": 5, "value": cta_primary_header_fontStyle})
                                                .then((res) => {
                                                  axios.post(`${URL}/theme/` + res.data.id + `/config`, {"theme_ss_id": 3, "theme_feature_id": 6, "value": cta_primary_header_fontSize})
                                                  .then((res) => {
                                                    axios.post(`${URL}/theme/` + res.data.id + `/config`, {"theme_ss_id": 3, "theme_feature_id": 7, "value": cta_primary_header_fontColor})
                                                    .then((res) => {
                                                      axios.post(`${URL}/theme/` + res.data.id + `/config`, {"theme_ss_id": 3, "theme_feature_id": 8, "value": cta_primary_header_bgColor})
                                                      .then((res) => {

                                                      })
                                                    })
                                                  })
                                                })
                                              })
                                            })
                                          })
