        })
      })

      console.log("bold:" + titulo_header_bold + " Itallic: " + titulo_header_itallic, titulo_header_underline, titulo_header_align, titulo_header_fontStyle, titulo_header_fontSize, titulo_header_fontColor, titulo_header_bgColor  )

    }

}

export const fetchThemesBegin = () => ({
  type: FETCH_THEMES_BEGIN
});

export const fetchThemesSuccess = themes => ({
    type: FETCH_THEMES_SUCCESS,
    payload: { themes }
});

export const fetchThemeSuccess = theme => ({
  type: FETCH_THEME_SUCCESS,
  payload: { theme }
});

export const fetchThemesFailure = error => ({
    type: FETCH_THEMES_FAILURE,
    payload: { error }
});

export function fetchThemes(){
    return dispatch => {
      dispatch(fetchThemesBegin());
      return axios.post(`${URL}/theme/search/10/1`,{"busca": ""})
        .then((response) => {
          var {data} = response;
          dispatch(fetchThemesSuccess(data));
          return data;
        })
        .catch(error => dispatch(fetchThemesFailure(error)));
    }
}

export function fetchTheme(id){
  return dispatch => {
    dispatch(fetchThemesBegin());
    return axios.get(`${URL}/theme/` + id + `/config`)
      .then((response) => {
        var {data} = response;
        dispatch(fetchThemeSuccess(data));
        return data;
      })
      .catch(error => dispatch(fetchThemesFailure(error)));
  }
}
