import React, { Component } from "react";
import {Link } from "react-router-dom";
import { connect } from "react-redux";
import Loader from "./Loader";
import { fetchGifts, fetchGiftCategories } from "../actions/giftActions";
import setSelect2 from "../assets/js/setSelect2";
import "select2";

const blockStyle = {
  "display": "block"
}


class PresentesAdmin extends Component {

  componentDidMount(){
    // setSelect2();
    this.props.dispatch(fetchGifts());
    this.props.dispatch(fetchGiftCategories());
  }

  render() {
    const divStyle = (src) => ({
      backgroundImage: 'url(' + src + ')'
    })
    const{ gifts } = this.props;
    const {loading, items, error} = gifts;
    const {giftsCategories} = gifts;
    if(giftsCategories){
      setSelect2();
    }
    return (
      <div className="wrap-content novo-tema presentes">

          <div className="content-title">
              <div className="flex flex-baseline flex-space">
                  <div>
                      <h1>Presentes</h1>
                      <h2>Edite, exclua ou crie novos presentes</h2>
                  </div>

                  <div>
                      <button className="gradient fullcolor novo-desk">
                        <Link to="/dashboard/presentes/novo-presente">
                          Novo presente
                        </Link>
                      </button>
                      <button className="gradient fullcolor novo-mob">+</button>
                  </div>
              </div>
          </div>

          <section className="card-options">
              <div>

                <div className="content-card-options">
                  <div className="header-card-options">
                      <h2 className="title">Buscar um presente</h2>
                      <p className="subtitle">Lorem ipsum dolor sit amet, consectetur adipisicing elit. Ex quas cum reprehenderit fuga repellat, maiores ut minus accusantium</p>
                  </div>
                </div>

                <div className="line"></div>

                <div className="main-options">

                  <div className="grid grid-input">

                    <div>
                        <label htmlFor="tipo-festa" className="label-theme">Nome do presente <i className="ng-info-circled"></i></label>
                        <input type="text" className="input-theme" name="tipo-festa" id="tipo-festa" placeholder="Digite o nome do presente que você quer busca" />
                    </div>

                    <div className="presente-categ-mob">
                        <label htmlFor="descricao-tema" className="label-theme">Categoria do presente<i className="ng-info-circled"></i> </label>
                        <div className="wrap-categoria-tema">
                            <div className="selecione flex flex-space flex-center">
                                <span>Selecione</span>

                                <i className="ng-down-open-big"></i>
                            </div>

                            <div className="main-categorias" style={blockStyle}>
                                <div className="triangle"></div>
                                <div className="item-categorias">
                                    <p className="title-categorias">Categorias atribuídas <i className="ng-info-circled"></i></p>
                                    <div className="tags-categorias"></div>
                                    <div className="line"></div>
                                </div>

                                <div className="select-categoria-tema">
                                    <select className="custom-select bg-white categoria-tema" data-select multiple>
                                      {
                                        giftsCategories ? giftsCategories.map((item) => {
                                          return (
                                            <option key={item.id} value={item.id}>{item.name}</option> 
                                          )
                                        }) : ""
                                      }
                                    </select>

                                    <div className="dropdown-categoria-tema"></div>
                                </div>
                            </div>
                        </div>

                    </div>
                    <button className="gradient fullcolor">Buscar presente</button>

                  </div>

                </div>

              </div>
          </section>

          <div className="cards-gift flex flex-wrap">

            {
              loading ? <Loader /> :
              error ? <p>Erro no servidor</p> :
              items.map((item) => {
                return (
                  <div key={item.id} className="card-gift  flex flex-column flex-space">
                    <div className="card-gift-thumb" style={divStyle(item.url)}></div>
                      <div className="flex flex-column">
                        <div className="card-gift-name">{item.name}</div>
                        <div className="card-gift-price">R${`${item.price}`.replace(".", ",")}</div>
                        <div className="card-gift-desc">{item.description.substring(0,100)}</div>
                    </div>
                    <div className="flex flex-space">
                      <div className="buttons">
                          <Link to={{
                            pathname: `/dashboard/presentes/editar-presente/${item.id}`,
                          }} className="gradient fullcolor edit">Editar item</Link>
                          <button className="gradient border bgwhite"><span>Excluir</span></button>
                      </div>
                    </div>
                  </div>
                )
              })
            }

          </div>

      </div>
    );
  }
}

const mapStateToProps = state => ({
  gifts: state.gifts
})


export default connect(mapStateToProps)(PresentesAdmin);
