import React, { Component } from "react";
import { Link } from "react-router-dom";

export default class Login extends Component {

  constructor(props) {
    super(props);
    this.state = {
      identifier: '',
      password: '',
      errors: {},
      loading: false
    };

    this.onSubmit = this.onSubmit.bind(this);
    this.onChange = this.onChange.bind(this);
  }

  onSubmit(e) {
    e.preventDefault();
  };

  onChange(e) {
    this.setState({
      [e.target.name]: e.target.value
    })
  };

  render() {
    const { identifier, password, errors } = this.state;
    return (
      <body className="login bg-login responsive">
        <div className="container">
          <main>

            <div className="txt-login">
                <h1 className="titulo title-mobile">Acesse sua conta</h1>
                <h2 className="subtitulo">Bem-vindo de volta!</h2>
                <div className=" icon rounded yellow shadow-20">
                    <i className="ng-user-alt"></i>
                </div>
                <p className="p-1">Ainda não tem uma conta?</p>
                <p className="p-2">Crie sua conta agora mesmo.</p>

                <Link to="cadastrar.html" className="gradient border hover-animation btn-criar-conta">
                    <span className="btn-txt-link-criar-conta">Criar conta</span>
                </Link>
            </div>

            <div className="form">
                <form onSubmit={this.onSubmit} className="card-purple">
                    <label for="" className="label-login">Digite seu email</label>
                    <input name="identifier" onChange={this.onChange} type="email" className="input-login shadow-20" placeholder="exemplo@email.com.br" required />

                    <label for="" className="label-login">Digite a sua senha</label>
                    <input name="password" onChange={this.onChange} type="password" className="input-login shadow-20" required />

                    <button className="gradient fullcolor comecar-festa"><span>Entrar</span><i className="ng-right-arrow-extend"></i></button>
                </form>

                <div className="div-facebook">
                    <button disable={isLoading} className="btn-facebook shadow-20">
                        <img src="../assets/imgs/facebook-logo.svg" alt="" className="facebook-svg" />
                        <span>Entrar usando o facebook</span>
                    </button>
                </div>

                <div className="div-esqueceu-senha">
                    <Link to="#" className="esqueceu-senha cyan">Esqueceu a sua senha? Receba uma nova no seu email</Link>
                </div>
            </div>

          </main>

          <p className="login-mobile">
              <span>Ainda não tem uma conta? </span>
              <Link to="cadastrar.html" className="cyan">Criar conta</Link>
          </p>

        </div>
      </body>
    );
  }
}
