import axios from "axios";
const URL   = "http://51.15.99.120:4700";

export const FETCH_THANKS_BEGIN   = 'FETCH_THANKS_BEGIN';
export const FETCH_THANKS_SUCCESS = 'FETCH_THANKS_SUCCESS';
export const FETCH_THANKS_FAILURE = 'FETCH_THANKS_FAILURE';


export const fetchThanksBegin = () => ({
    type: FETCH_THANKS_BEGIN
});

export const fetchThanksSuccess = thanks => ({
    type: FETCH_THANKS_SUCCESS,
    payload: { thanks }
});

export const fetchThanksFailure = error => ({
    type: FETCH_THANKS_FAILURE,
    payload: { error }
});

export function fetchThanks(event_id, obj){
    return dispatch => {
      dispatch(fetchThanksBegin());
      return axios.get(`${URL}/gift/salutation/${event_id}/text`, obj)
        .then((response) => {
          var {data} = response;
          console.log(response)
          dispatch(fetchThanksSuccess(data));
          return data;
        })
        .catch(error => dispatch(fetchThanksFailure(error)));
    }
}
