import React, { Component } from "react";
import { connect } from "react-redux";
import { Link } from "react-router-dom";
import $ from 'jquery';

import TemaComponent from "./TemaComponent";
import {fetchThemes} from "../actions/themesActions";
import {fetchGetEvent} from "../actions/getEventAction";

import Select from 'react-select';

class SiteFesta extends Component {
    componentDidMount(){
        this.props.fetchThemes( null, null, null, null, null, 1);
        this.props.fetchGetEvent(this.props.match.params.id)
    }


    handleChange = (newValue: any, actionMeta: any ) => {
		// console.group('Value Changed');
		console.log(newValue);
		if(newValue.label === "Casamento"){
			$(".theme-site-casamento-welcome").show(500).css("display", "flex");
			$(".theme-site-casamento-about").show(500);
			$(".theme-site-about").hide(500);
			$(".theme-site-casamento-padrinhos").show(500).css("display", "flex");
			$("#titulo-header").text("Zian e Hana");
		}else{
			$(".theme-site-casamento-welcome").hide(500);
			$(".theme-site-casamento-about").hide(500);
			$(".theme-site-casamento-padrinhos").hide(500)
			$("#titulo-header").text("Aniversário do José");
		}
		$("input[name=tema-thematics]").val(newValue.value);

		if ( 'create-option' === actionMeta.action ) {
			var last_new_option;
			var last_new_index;
			if ( newValue.hasOwnProperty( '__isNew__' ) ) {
				last_new_option = newValue.value;
			}
			this.props.fetchSetCategories( 'thematic', last_new_option ).then( ( response ) => {
					newValue.value = response.id;

					this.props.dispatch( fetchCategories( 'thematics' ) );
			});
		}
		// console.log( actionMeta );
		// console.log(`action: ${actionMeta.action}`);
		// console.groupEnd();
	}

    render() {
        const {items} = this.props.themes;
        const {EVENTO} = this.props.event.items
        console.log(items)

		var categories_options = [];

		if ( undefined !== items ) {
            items.map( ( theme ) => {
                    var id = theme.id;
                    var name = theme.name;
                    categories_options.push( { value: id, label : name } );
                    console.log(name)
				})
		}
        return(
            <div className="site-aniversario">
                <div className="container">
                    <div className="summary without-divider">
                        <div className="left">
                            <h2 className="title">Site da Festa</h2>
                            <p className="subtitle">Fusce dui mauris, efficitur non auctor in, ornare id erat.</p>
                        </div>
                    </div>

                    <div className="flex flex-space my50">

                        <div className="config">
                            <div className="config-accordion without-toggle">

                                <div className="config-item">

                                    <div className="config-item-title flex flex-center flex-space">
                                        <p>Tema</p>
                                        <i className="ng-down-open"></i>
                                    </div>
                                    <div className="config-item-content tema">
                                        <div className="divider"></div>
