import React, { Component } from "react";
import { Link } from "react-router-dom";
import { Field, reduxForm } from 'redux-form';

import HeaderLogin from "../components/HeaderLogin";

import Facebook from "../assets/imgs/facebook-logo.svg";

class Login extends Component {

  submit = (values) => {
    console.log(values);
  }

  render() {
    const { handleSubmit } = this.props;
    return (
      <body className="login bg-login responsive">
        <div className="container">
        <HeaderLogin />
          <main>

            <div className="txt-login">
                <h1 className="titulo title-mobile">Acesse sua conta</h1>
                <h2 className="subtitulo">Bem-vindo de volta!</h2>
                <div className=" icon rounded yellow shadow-20">
                    <i className="ng-user-alt"></i>
                </div>
                <p className="p-1">Ainda não tem uma conta?</p>
                <p className="p-2">Crie sua conta agora mesmo.</p>

                <Link to="/cadastro" className="gradient border hover-animation btn-criar-conta">
                    <span className="btn-txt-link-criar-conta">Criar conta</span>
                </Link>
            </div>

            <div className="form">
                <form onSubmit={ handleSubmit(this.submit) } className="card-purple">
                <Field
                  name="email"
                  component= "input"
                  className="input-login shadow-20"
                  type="email"
                  placeholder="exemplo@email.com.br"
                  required="true"
                />
                <Field
                  name="password"
                  component= "input"
                  className="input-login shadow-20"
                  type="email"
                  placeholder="exemplo@email.com.br"
                  required="true"
                />
                  {
                    //  <label for="" className="label-login">Digite seu email</label>
                    //   <input name="identifier" type="email" className="input-login shadow-20" placeholder="exemplo@email.com.br" required />
                  }
                    {
                      // <label for="" className="label-login">Digite a sua senha</label>
                      // <input name="password" type="password" className="input-login shadow-20" required />
                    }

                    <button className="gradient fullcolor comecar-festa"><span>Entrar</span><i className="ng-right-arrow-extend"></i></button>
                </form>

                <div className="div-facebook">
                    <button className="btn-facebook shadow-20">
                        <img src={Facebook} alt="" className="facebook-svg" />
                        <span>Entrar usando o facebook</span>
                    </button>
                </div>

                <div className="div-esqueceu-senha">
                    <Link to="/recuperar-senha" className="esqueceu-senha cyan">Esqueceu a sua senha? Receba uma nova no seu email</Link>
                </div>
            </div>

          </main>

          <p className="login-mobile">
              <span>Ainda não tem uma conta? </span>
              <Link to="/cadastro" className="cyan">Criar conta</Link>
          </p>

          <footer>
              <div className="flex flex-column ">
                  <div className="flex flex-space">
                      <div className="menu-footer"><Link to="">Home</Link><span>|</span><Link to="#">Buscar uma festa</Link><span>|</span><Link to="/duvidas">Dúvidas</Link><span>|</span><Link to="/contato">Contato</Link></div>
                      <div className="copyright">® 2019 NETGIFT - CPNJ 99.304.401/0001-00 - <Link to="/termos">TERMOS DE SERVIÇO</Link>.</div>

                      <p className="copyright copyright-mobile">® 2019 NETGIFT - CPNJ 99.304.401/0001-00</p>
                  </div>
              </div>
          </footer>

        </div>
      </body>
    );
  }
}

export default reduxForm({
  form: 'signin'
})(Login);
