import React, { Component } from "react";
import {Link } from "react-router-dom";
import { connect } from "react-redux";
import { fetchThemes } from "../actions/themesActions";
import { fetchCategories, fetchSetCategories } from "../actions/categoriesActions";
import {Field, reduxForm} from 'redux-form';
import Loader from "./Loader";
import Loading from "../assets/imgs/loader.gif";
import $ from 'jquery';

import "../assets/js/themes-filters.js";

import {bgImage, bgColor, displayNone, defaultStyle} from "./styleFunctions";


class TemasAdmin extends Component {

  componentDidMount(){
    this.props.dispatch(fetchThemes());
    this.props.dispatch( fetchCategories( 'categories' ) );
  }

    submit = ( item ) => {
        var category_ids = [];
        var search       = item.search;

        if ( undefined !== item.categories ) {
            item.categories.map( ( categ, index ) => {
                if ( categ ) {
                    category_ids.push( index );
                } 
            });
        }

        var d = new Date();
        var date_start = null;
        var date_end   = null;

        var one_day    = 86400000;

        switch ( item.period ) {
            case 'hoje':
                var month  = d.getMonth() + 1;
                month      = 10 > month ? '0' + month : month;
                date_start = d.getFullYear() + '-' + month + '-' + d.getDate();
                d          = new Date( d.getTime() + one_day );
                month      = d.getMonth() + 1;
                month      = 10 > month ? '0' + month : month;
                date_end   = d.getFullYear() + '-' + month + '-' + d.getDate();
                break;
            case 'ontem':
                d          = new Date( d.getTime() - one_day );
                var month  = d.getMonth() + 1;
                month      = 10 > month ? '0' + month : month;
                date_start = d.getFullYear() + '-' + month + '-' + d.getDate();
                d          = new Date( d.getTime() + one_day );
                month      = d.getMonth() + 1;
                month      = 10 > month ? '0' + month : month;
                date_end   = d.getFullYear() + '-' + month + '-' + d.getDate();
                break;
            case 'semana':
                d          = new Date( d.getTime() + one_day );
                var month  = d.getMonth() + 1;
                month      = 10 > month ? '0' + month : month;
                date_end   = d.getFullYear() + '-' + month + '-' + d.getDate();
                d          = new Date( d.getTime() - ( d.getDay() * one_day ) );
                month      = d.getMonth() + 1;
                month      = 10 > month ? '0' + month : month;
                date_start = d.getFullYear() + '-' + month + '-' + d.getDate();
                break;
            case 'mes':
                d          = new Date( d.getTime() + one_day );
                var month  = d.getMonth() + 1;
                month      = 10 > month ? '0' + month : month;
                date_start = d.getFullYear() + '-' + month + '-01';
                date_end   = d.getFullYear() + '-' + month + '-' + d.getDate();
                break;
            case 'custom':
                date_start = item.custom_date_from;
                date_end   = item.custom_date_to;
                break;
        }

        category_ids = 0 !== category_ids.length ? category_ids : null;

        this.props.dispatch( fetchThemes( search, category_ids, date_start, date_end ) );
    }

    state = {
        current_categories: [],
    };

  render() {

    var sessions = {};

    sessions['header'] = {};
    sessions['header']["titulo"] = {};
    sessions['header']["data"] = {};
    sessions['header']["cta_presentear"] = {};
    sessions['header']["cta_confirmar_presenca"] = {};
    sessions['header']["faltam_n_dias"] = {};
    sessions['header']["background"] = {};

    sessions['saudacao'] = {};
    sessions['saudacao']["titulo"] = {};
    sessions['saudacao']["descricao"] = {};
    sessions['saudacao']["background"] = {};

    sessions['localizacao'] = {};
    sessions['localizacao']["titulo"] = {};
    sessions['localizacao']["data"] = {};
    sessions['localizacao']["endereco"] = {};
    sessions['localizacao']["background"] = {};

    sessions['fotos'] = {}
    sessions['fotos']["titulo"] = {};
    sessions['fotos']["background"] = {};

    sessions['rodape'] = {};
    sessions['rodape']["titulo"] = {};
    sessions['rodape']["cta_presentear"] = {};
    sessions['rodape']["cta_confirmar_presenca"] = {};
    sessions['rodape']["faltam_n_dias"] = {};
    sessions['rodape']["background"] = {};

    if ( undefined !== this.props.categories && 0 !== this.props.categories.length ) {
        this.state.current_categories = this.props.categories;
    }

    const{ themes, handleSubmit } = this.props;
    const {items, error, loading} = themes;

    var current_themes = '' !== items ? items : [];

    if(items){
        item.sessions.map((sess) => {

            sess.sub_sessions.map((subSess) => {
                subSess.features.map( ( feature ) => {
                    sessions[sess.session][subSess.sub_session][feature.name] = feature.value;
                });
            })
        })

    }

    console.log(sessions['header']["background"]);

    return (
      <div className="wrap-content dashboard temas">

        <div>
            <div className="content-title">
                <div className="flex flex-end flex-space">
                    <div>
                        <h1>Temas</h1>
                        <h2>Edite, exclua ou crie novos temas</h2>
                    </div>
                    <button className="gradient fullcolor">
                        <Link to="temas/novo-tema">
                            Novo tema
                        </Link>    
                    </button>
                </div>
            </div>

            <div className="wrap-body">
                <form onSubmit={handleSubmit( this.submit )} className="sidebar refinar flex flex-column theme-filter-form" name="theme_filter_form">
                        <button id="submit-form-button-hidden" hidden>Filtrar</button>
                        <div className="title black">Refinar por</div>

                        <div className="sidebar-item">
                            <div className="title">Nome</div>
                            <Field
                                name="search"
                                component="input"
                                className="search"
                                id="search-field"
                                type="text"
                                placeholder="Digite o nome do template"
                                />
                        </div>

                        <div className="sidebar-item">
                            <div className="title">Categorias</div>
                            {
                                this.state.current_categories.map( ( category, i ) => {
                                    var id = category.id;
                                    var name = category.name;
                                    return(
                                        <div key={i} className="filter-item">
                                            <Field
                                                name={"categories[" + id + "]" }
                                                component="input"
                                                className={"categories category-" + id}
                                                type="checkbox"
                                                id={id}
                                                value={id}
                                                />

                                            <label htmlFor={id}><span>{name}</span><span className="count"></span></label>
                                        </div>
                                    )
                                })
                            }

                        </div>

                        <div className="sidebar-item">
                            <div className="title">Data de Adição</div>

                            <div className="filter-item">
                                <Field
                                    name={"period"}
                                    component="input"
                                    className={"periods period-hoje"}
                                    type="radio"
                                    id="hoje"
                                    value="hoje"
                                    />
                                <label htmlFor="hoje"><span>Hoje</span></label>
                            </div>

                            <div className="filter-item">
