import React, { Component } from "react";
import setSelect2 from "../assets/js/setSelect2";
import { connect } from "react-redux";
import Extrato from "./ExtratoAdmin";
import GridCards from "./GridCards";
import {fetchUserCount} from "../actions/usersActions";
import {fetchUserSites} from "../actions/usersActions";

import $ from "jquery";

class Dashboard extends Component {

    componentDidMount(){
        setSelect2();
        this.props.dispatch(fetchUserCount("Semanal"));
        this.props.dispatch(fetchUserSites("Semanal"));
      }

    render() {

        $(document).on("change", "#filter-date", function(){
            console.log($(this).val());
        })


        const {total, totalPassado, loading, error, porcentagem, totalSites} = this.props.usersCount;
        var up = true;

        if(total <= totalPassado){
            up = false
        }

        console.log(this.props.usersCount);

        return (
            <div className="wrap-content home">
                <div className="content-title">
                    <div className="flex flex-baseline flex-space">
                        <div>
                            <h1>Dashboard</h1>
                            <h2>Você está no painel principal</h2>
                        </div>
                        <div className="filter-admin">
                            <select name="filter_date" id="filter-date" className="select-filter-admin select-filter-date">
                                <option defaultValue="day">Diário</option>
                                <option defaultValue="week" defaultValue>Semanal</option>
                                <option defaultValue="month">Mensal</option>
                            </select>
                        </div>
                    </div>
                </div>
                <div className="wrap-body">
                    <GridCards 
                        totalUser={total}
                        totalUserPassado={totalPassado ? totalPassado : "--"}
                        porcentagem={porcentagem ? porcentagem : "--"}
                        totalSites={totalSites ? totalSites : "--"}
                        loading={loading}
                    />
                </div>
                <Extrato />
            </div>
        )
    }
}
const mapStateToProps = state => ({
    extrato: state.extrato,
    usersCount: state.usersCount
  })

export default connect(mapStateToProps)(Dashboard);
