import $ from 'jquery';

if ($('.usuarios').length > 0) {
    var $gridFestas = $('.grid-festas').masonry({
        itemSelector: '.box-festas',
        gutter: 55,
        horizontalOrder: true,
        columnWidth: '.box-festas-sizer',
        percentPosition: true
    });

    $gridFestas.masonry('layout');

}

export function reloadUsuariosCard() {
    if ($("body").hasClass("usuarios")) {
        if ($(".dashboard").hasClass("aside-active")) {
            setTimeout(function () {
                $gridFestas.masonry('layout');
                $gridFestas.masonry({
                    gutter: 38
                });
            }, 350);
        } else {
            setTimeout(function () {
                $gridFestas.masonry('layout');
                $gridFestas.masonry({
                    gutter: 55
                });
            }, 350);
        }
    }
}
