import axios from "axios";
export const FETCH_PRODUCTS_BEGIN   = 'FETCH_PRODUCTS_BEGIN';
export const FETCH_PRODUCTS_SUCCESS = 'FETCH_PRODUCTS_SUCCESS';
export const FETCH_PRODUCTS_FAILURE = 'FETCH_PRODUCTS_FAILURE';

export const fetchProductsBegin = () => ({
  type: FETCH_PRODUCTS_BEGIN
});

export const fetchProductsSuccess = gifts => ({
  type: FETCH_PRODUCTS_SUCCESS,
  payload: { gifts }
});

export const fetchProductsFailure = error => ({
  type: FETCH_PRODUCTS_FAILURE,
  payload: { error }
});

export function fetchProducts() {
  return dispatch => {
    dispatch(fetchProductsBegin());
    return axios.get(`${URL}/gift`)
      .then((response) => {
        var {data} = response;
        dispatch(fetchProductsSuccess(data));
        return data;
        })
      .catch(error => dispatch(fetchProductsFailure(error)));
  };
}

export function fetchProduct(id){
  return dispatch => {
    dispatch(fetchProductsBegin());
    return axios.get(`${URL}/gift/` + id)
      .then((response) => {
        var {data} = response;
        dispatch(fetchProductsSuccess(data));
        return data;
      })
      .catch(error => dispatch(fetchProductsFailure(error)));
  }
}

export function sendGift({description, nome_presente, image, category_id, price}, history){
  return async dispatch => {
    dispatch(fetchProductsBegin());
    axios.post(`${URL}/gift`, { "description" : description, "image" : image, "price" : price, "category_id" : category_id }, config)
      .then((response) => {
        var {data} = response;
        dispatch(fetchProductsSuccess(data));
        return data;
      })
      .catch(error => dispatch(fetchProductsFailure(error)));
  }
}

export function editGift({descricao_presente, nome_presente, picture, presente_categoria, valor_presente}, history){
  return async dispatch => {
    dispatch(fetchProductsBegin());
    axios.post(`${URL}/gift`, { "description" : descricao_presente, "price" : valor_presente, "category_id" : presente_categoria, "image" : picture }, config)
      .then((response) => {
        var {data} = response;
        dispatch(fetchProductsSuccess(data));
        return data;
      })
      .catch(error => dispatch(fetchProductsFailure(error)));
  }
}
