import React, { Component } from 'react';
import { connect } from "react-redux";
import { fetchExtrato } from "../actions/extratoActions";
import {fetchUserCount} from "../actions/usersActions";
import Loader from "./Loader";

class GrifCards extends Component{

    componentDidMount(){
        this.props.dispatch( fetchExtrato() );
        this.props.dispatch(fetchUserCount());
    }

    render(){
        console.log(this.props.usersCount)
        const {total, totalPassado, loading, error, porcentagem} = this.props.usersCount;
        var up = true;

        if(total <= totalPassado){
            up = false
        }

        return(
            <div className="dash-cards grid">
                <div className="card-dash">
                    <small>Visitas</small>
                    <div className="flex flex-center">
                        <p className="number">615</p>
                        <p className="taxa up">6% <span></span></p>
                        <div className="triangle triangle-up"></div>
                    </div>
                    <p className="historico">Semana anterior: <span>514</span></p>
                </div>
                <div className="card-dash">
                    <small>Novos Usuários</small>
                    <div className="flex flex-center">
                        <p className="number">{
                            loading ? <Loader /> : 
                            error ? "Error no servidor" : `${total}`
                        }</p>
                        <p className={up ? "taxa up" : "taxa down"}>{totalPassado ? porcentagem : "--"}% <span></span></p>
                        <div className={up ? "triangle triangle-up" : "triangle triangle-down"}></div>
                    </div>
                    <p className="historico">Semana anterior: <span>{
                        totalPassado ? totalPassado : "--"
                    }</span></p>
                </div>
                <div className="card-dash">
                    <small>Novos Sites</small>
                    <div className="flex flex-center">
                        <p className="number">31</p>
                        <p className="taxa up">115% <span></span></p>
                        <div className="triangle triangle-up"></div>
                    </div>
                    <p className="historico">Semana anterior: <span>13</span></p>
                </div>
                <div className="card-dash">
                    <small>Faturamento</small>
                    <div className="flex flex-center">
                        <p className="number">R$ 31.913,90</p>
                        <p className="taxa up">15% <span></span></p>
                        <div className="triangle triangle-up"></div>
                    </div>
                    <p className="historico">Semana anterior: <span>R$ 12.313,50</span></p>
                </div>
                <div className="card-dash">
                    <small>Pagamentos</small>
                    <div className="flex flex-center">
                        <p className="number">R$ 9.830,13</p>
                        <p className="taxa up">11% <span></span></p>
                        <div className="triangle triangle-up"></div>
                    </div>
                    <p className="historico">Semana anterior: <span>R$ 7.919,80</span></p>
                </div>
                </div>
        )
    }

}

const mapStateToProps = state => ({
    extrato: state.extrato,
    usersCount: state.usersCount
  })

export default connect(mapStateToProps)(GrifCards);
