import axios from "axios";
export const FETCH_GIFT_BEGIN   = 'FETCH_GIFT_BEGIN';
export const FETCH_GIFT_SUCCESS = 'FETCH_GIFT_SUCCESS';
export const FETCH_GIFT_FAILURE = 'FETCH_GIFT_FAILURE';

const URL   = "http://51.15.99.120:4700";
const urlWP = "http://www.localkindle.com.br/netgift_api/wp-json/ntgift/api";

const config = {
  headers: {"Access-Control-Allow-Origin": "*"},
}

export const fetchGiftsBegin = () => ({
  type: FETCH_GIFT_BEGIN
});

export const fetchGiftsSuccess = gifts => ({
  type: FETCH_GIFT_SUCCESS,
  payload: { gifts }
});

export const fetchGiftsFailure = error => ({
  type: FETCH_GIFT_FAILURE,
  payload: { error }
});

export const sendGiftSuccess = gift => ({
  type: SEND_GIFT_SUCCESS,
  payload: { gifts }
})

export function fetchGifts() {
  return dispatch => {
    dispatch(fetchGiftsBegin());
    return axios.get(`${URL}/gift`)
      .then((response) => {
        var {data} = response;
        dispatch(fetchGiftsSuccess(data));
        return data;
        })
      .catch(error => dispatch(fetchGiftsFailure(error)));
  };
}

export function fetchGift(id){
  return dispatch => {
    dispatch(fetchGiftsBegin());
    return axios.get(`${URL}/gift/` + id)
      .then((response) => {
        var {data} = response;
        dispatch(fetchGiftsSuccess(data));
        return data;
      })
      .catch(error => dispatch(fetchGiftsFailure(error)));
  }
}

export function sendGift({description, nome_presente, image, category_id, price}, history){
  return async dispatch => {
    dispatch(fetchGiftsBegin());
    axios.post(`${URL}/gift`, { "description" : description, "image" : image, "price" : price, "category_id" : category_id }, config)
      .then((response) => {
        var {data} = response;
        dispatch(sendGiftSuccess(data));
        return data;
      })
      .catch(error => dispatch(fetchGiftsFailure(error)));
  }
}

export function editGift({descricao_presente, nome_presente, picture, presente_categoria, valor_presente}, history){
  return async dispatch => {
    dispatch(fetchGiftsBegin());
    axios.post(`${URL}/gift`, { "description" : descricao_presente, "price" : valor_presente, "category_id" : presente_categoria, "image" : picture }, config)
      .then((response) => {
        var {data} = response;
        dispatch(fetchGiftsSuccess(data));
        return data;
      })
      .catch(error => dispatch(fetchGiftsFailure(error)));
  }
}
