import React, { Component } from "react";
import setSelect2 from "../assets/js/setSelect2";
import { connect } from "react-redux";
import Extrato from "./ExtratoAdmin";
import GridCards from "./GridCards";
import {fetchUserCount, fetchUserSites} from "../actions/usersActions";
import { fetchExtratoAbstract } from "../actions/extratoActions";

import $ from "jquery";

class Dashboard extends Component {

    componentDidMount(){
        // setSelect2();
        this.props.fetchUserCount();
        this.props.fetchUserSites();
        this.props.fetchExtratoAbstract(null, null, null, null, null, null, 1, 1);
      }

      change = () => {
        let periodo = $("#filter-date").val();
        console.log(periodo)
        this.props.fetchUserCount(periodo);
        if(periodo === "diario"){
            this.props.fetchExtratoAbstract(null, null, null, null, null, null, 1, 1);
        }
        if(periodo === "semanal"){
            this.props.fetchExtratoAbstract(null, null, null, null, null, null, 1, 7);
        }
        if(periodo === "mensal"){
            this.props.fetchExtratoAbstract(null, null, null, null, null, null, 1, 30);
        }
      }

    render() {
        const {total, totalPassado, loading, error, porcentagem, totalSites, totalSitePassado, porcentagemSites} = this.props.usersCount;
        const loadingExtrato = this.props.extrato.loading;
        const {extrato} = this.props.extrato;
        const {dashboard} = extrato;
        const {extratoOld} = this.props.extrato;
        const extratoAbstract = this.props.extratoAbstract;

        // console.log(extratoOld)

        if(extratoOld){
            console.log(totalOld)
        }


        var faturamento;
        var recebido;
        if(extratoAbstract){
            faturamento = extratoAbstract.dashboard.total.toFixed(2).replace(".", ",")
            recebido = extratoAbstract.dashboard.netgift.recebido.toFixed(2).replace(".", ",")
        }else{
            faturamento = "--"
            recebido = "--"
        }

        return (
            <div className="wrap-content home">
                <div className="content-title">
                    <div className="flex flex-baseline flex-space">
                        <div>
                            <h1>Dashboard</h1>
                            <h2>Você está no painel principal</h2>
                        </div>
                        <div className="filter-admin">
                            <select onChange={() => this.change()} name="filter_date" id="filter-date" className="select-filter-admin select-filter-date">
                                <option value="diario">Diário</option>
                                <option value="semanal">Semanal</option>
                                <option value="mensal">Mensal</option>
                            </select>
                        </div>
                    </div>
                </div>
                <div className="wrap-body">
                    <GridCards 
                        totalUser={total}
                        totalUserPassado={totalPassado}
                        porcentagem={porcentagem ? porcentagem : "--"}
                        totalSites={totalSites ? totalSites : "--"}
                        totalSitePassado={totalSitePassado ? totalSitePassado : "--"}
                        porcentagemSites={porcentagemSites ? porcentagemSites : "--"}
                        loading={loading}
                        faturamento={dashboard ? faturamento : "--"}
                        recebido={dashboard ? recebido : "--"}
                    />
                </div>
                <Extrato />
            </div>
        )
    }
}
const mapStateToProps = state => ({
    extrato: state.extrato,
    usersCount: state.usersCount,
    extrato: state.extrato,
    extratoAbstract : state.extrato.extratoAbstract
  })

export default connect(mapStateToProps, {fetchUserCount, fetchUserSites, fetchExtratoAbstract})(Dashboard);
