import axios from "axios";
export const AUTHENTICATED = "authenticated_user";
export const UNAUTHENTICATED = "unauthenticated_user";
export const AUTHENTICATION_ERROR = "authentication_error";
export const GET_GIFT = "get_gift";

const URL = "http://51.15.99.120:4700";

const config = {
  headers: {"Access-Control-Allow-Origin": "*"}
}

export function signInAction({ email, password }, history) {
  return async dispatch => {
    try {
      const res = await axios.post(`${URL}/login`, { email, password }, config);
      dispatch({ type: AUTHENTICATED });
      localStorage.setItem("user", res.data);
      history.push("/dashboard");
    } catch (error) {
      dispatch({
        type: AUTHENTICATION_ERROR,
        payload: "Invalid email or password"
      });
    }
  };
}

export function getPresents(){
  return async dispatch => {
    return axios.get(`${URL}/gift`)
      .then((res) => {
          dispatch({type: GET_GIFT, gifts: res.data});
      })
  }
}
