import React, { Component } from "react";
import { Link } from "react-router-dom";
import { connect } from "react-redux";

import ConfirmarPresenca from '../components/ConfirmarPresenca';
import { fetchGetEvent } from '../actions';
import { fetchThemeConfigurations } from '../actions/themeConfigurationsAction';

import logo from '../assets/imgs/logo.png';
import img1 from '../assets/imgs/jogo-de-cama.png';
import img2 from '../assets/imgs/maquina-de-lavar.png';

import '../assets/js/script';
import '../assets/js/plugins';

import '../assets/css/style-theme.css';



class HomeConvidado extends Component {
    componentDidMount() {
        this.props.dispatch(fetchGetEvent(this.props.match.params.id));
    }

    render() {

        const { id } = this.props.match.params;
        const event_home_link = "/festa/" + id;
        const event_history_link = "/festa/" + id + "/#nossa-historia";
        const event_godparents_link = "/festa/" + id + "/#padrinhos";
        const event_location_link = "/festa/" + id + "/#localizacao";
        const event_pictures_link = "/festa/" + id + "/#fotos";
        const event_gifts_link = "/festa/" + id + "/lista-de-presentes";
        const event_cart_link = "/festa/" + id + "/carrinho";
        const { error, loading, fetchThemeConfigurations, event } = this.props;
        let street = '';
        let number = '';

        if(event !== undefined) {
            var sub_session_items = {};
            event.template.sessions.map( function( session ) {
                var current_session = session.session;
                sub_session_items[current_session] =  {};

                session.sub_sessions.map( function ( sub_session ) {
                    var current_sub_session = sub_session.sub_session;
                    sub_session_items[current_session][current_sub_session] =  {};
                    sub_session.features.map( function( feature ) {
                        sub_session_items[current_session][current_sub_session][ feature.name ] = feature.value;
                    });
                });
            });

            street = event.street;
        }


        function MenuFesta(props) {
            const tipoFesta = props.tipoFesta;
            if (tipoFesta === 4) {
              return <ul className="flex">
                        <li><Link to={event_history_link} className="white-underline link-menu">Nossa História</Link></li>
                        <li><Link to={event_godparents_link} className="white-underline link-menu">Padrinhos</Link></li>
                        <li><Link to={event_location_link} className="white-underline link-menu">Local da Festa</Link></li>
                        <li><Link to={event_pictures_link} className="white-underline link-menu">Nossas fotos</Link></li>

                        <li className="logo-list">
                            <Link to={event_home_link}>
                                <img src={logo} alt="NetGift" />
                            </Link>
                        </li>
                    </ul>;
            }
            return <ul className="flex">
                        <li><Link to={event_home_link} className="white-underline link-menu">Início</Link></li>
                        <li><Link to={event_location_link} className="white-underline link-menu">Local da Festa</Link></li>
                        <li><Link to={event_pictures_link} className="white-underline link-menu">Nossas fotos</Link></li>

                        <li className="logo-list">
                            <Link to={event_home_link}>
                                <img src={logo} alt="NetGift" />
                            </Link>
                        </li>
                    </ul>;
        }

        function AvatarFesta(props) {
            const tipoFesta = props.tipoFesta;
            if (tipoFesta !== 4) {
                return <div className="container-editor">
                            <div id="avatar-about" className="element avatar" ></div>
                        </div>
            }

            return false;
        }

        function SectionCasal(props) {
            const tipoFesta = props.tipoFesta;
            if (tipoFesta === 4) {
                return <div className="content-couple flex-column flex" id="nossa-historia">
                            <div className="photo-couple flex">
                                <img src={img1} alt="img 1" />
                                <img src={img2} alt="img 2" />
                            </div>

                            <p id="titulo-about" className="element title-content">Nossa história</p>

                            <p className="text-couple">"Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat. Duis aute irure dolor in reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla pariatur. Excepteur sint occaecat cupidatat non proident, sunt in culpa qui officia deserunt mollit anim id est laborum."</p>
                        </div>

            }

            return false;
        }

        function SectionPadrinhos(props) {
            const tipoFesta = props.tipoFesta;
            if (tipoFesta === 4) {
                    return  <div className="theme-site-godparents" id="padrinhos">
                                <div className="content-godparents flex flex-column">
                                    <div className="text-main-godparents">
                                        <h3 id="titulo-about" className="element title-content">Nossa padrinhos</h3>

                                        <p className="text-couple">"Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore</p>
                                    </div>

                                    <div className="godparents flex">
                                        <div className="godparent flex flex-column flex-center">
                                            <div className="photo-godparents">
                                                <img src={img1} alt="img 1" />
                                                <img src={img2} alt="img 2" />
                                            </div>

                                            <div className="text-godparents">
                                                <h3>Victor e Luiza</h3>

                                                <p>Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut </p>
                                            </div>
                                        </div>

                                        <div className="godparent flex flex-column flex-center">
                                            <div className="photo-godparents">
                                                <img src={img1} alt="img 1" />
                                                <img src={img2} alt="img 2" />
                                            </div>

                                            <div className="text-godparents">
                                                <h3>Victor e Luiza</h3>

                                                <p>Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut </p>
                                            </div>
                                        </div>

                                        <div className="godparent flex flex-column flex-center">
                                            <div className="photo-godparents">
                                                <img src={img1} alt="img 1" />
                                                <img src={img2} alt="img 2" />
                                            </div>

                                            <div className="text-godparents">
                                                <h3>Victor e Luiza</h3>

                                                <p>Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut </p>
                                            </div>
                                        </div>
                                    </div>
                            </div>
                        </div>
            }

            return false;
        }

      return (
        <div className="w100 theme-site convidado area-convidado">

            <ConfirmarPresenca />

            <div className="theme-site-header">
               <header className="webdoor">
                    <div className="container">
                        <div className="menu flex flex-space flex-center">
                            <nav className="flex">
                                <Link to={event_home_link} className="logo"><img src={logo} alt="NetGift" /></Link>
                                <MenuFesta tipoFesta={event !== undefined ? event.template.thematics[0].thematic_id: ''} />     
                            </nav>

                            <div className="nav-right flex flex-center">
                                <button className="btn toggle-modal-confirmar-presenca" data-modal="confirmar-presenca"><span className="link-menu">Confirmar presença</span></button>
                                <Link to={event_gifts_link} className="btn gradient border hover-animation"><span>Lista de presentes</span></Link>

                                <Link to={event_cart_link} >
                                    <button className="btn-carrinho gradient fullcolor">
                                        <i className="ng-shopping-cart-alt"></i>
                                    </button>
                                </Link>

                                <div className="menu-mobile open-menu">
                                    <span className="menu-icon"></span>
                                </div>
                            </div>
                        </div>
                    </div>
                </header>

                <div className="container-editor">

                    <p id="titulo-header" 
                        style={{  
                            color: event !== undefined ? sub_session_items.header.titulo.texto_fonte_cor : '', 
                            fontSize: event !== undefined ? sub_session_items.header.titulo.texto_fonte_tamanho + 'px' : '', 
                            background: event !== undefined ? sub_session_items.header.titulo.texto_fundo_cor : ''
                        }}

                        className={
                            event !== undefined ? 
                            sub_session_items.header.titulo.alinhamento + ' ' + 
                            sub_session_items.header.titulo.italico + ' ' + sub_session_items.header.titulo.negrito + ' ' + sub_session_items.header.titulo.font  : 'element'
                        }
                    >
                        {
                            event !== undefined ? event.name: ''
                        }
                    </p>
                </div>
                <div className="container-editor">
                    <p id="data-header"
                        style={{  
                            color: event !== undefined ? sub_session_items.header.data.texto_fonte_cor : '', 
                            fontSize: event !== undefined ? sub_session_items.header.data.texto_fonte_tamanho + 'px' : '', 
                            background: event !== undefined ? sub_session_items.header.data.texto_fundo_cor : ''
                        }}

                        className={
                            event !== undefined ? 
                            sub_session_items.header.data.alinhamento + ' ' + 
                            sub_session_items.header.data.italico + ' ' + sub_session_items.header.data.negrito + ' ' + sub_session_items.header.data.font  : 'element'
                        }
                    >
                        10 de abril de 2019
                    </p>
                </div>
                <div className="container-editor">
                    <Link id="cta-primary-header" to={event_gifts_link}
                        style={{  
                            color: event !== undefined ? sub_session_items.header.cta_presentear.texto_fonte_cor : '', 
                            fontSize: event !== undefined ? sub_session_items.header.cta_presentear.texto_fonte_tamanho + 'px' : '', 
                            background: event !== undefined ? sub_session_items.header.cta_presentear.texto_fundo_cor : ''
                        }}

                        className={
                            event !== undefined ? 
                            sub_session_items.header.cta_presentear.alinhamento + '  ' + sub_session_items.header.cta_presentear.italico + ' ' + sub_session_items.header.cta_presentear.negrito + ' ' + sub_session_items.header.cta_presentear.font  + ' btn-cta btn-cta-primary' : 'btn-cta btn-cta-primary'
                        }
                    >
                        Presentear aniversariante
                    </Link>
                </div>
                <div className="container-editor">
                    <p id="cta-secondary-header" className=" btn-cta btn-cta-secondary" data-modal="confirmar-presenca">Confirmar presença</p>
                </div>
                <div className="container-editor">
                    <p id="contagem-header" className="element">Faltam 00 dias</p>
                </div>
            </div>
            <div className="theme-site-about">

                <AvatarFesta tipoFesta={event !== undefined ? event.template.thematics[0].thematic_id: ''} />

                <div className="container-editor">
                    <p id="titulo-about" className="element title-content">Que bom que você está aqui</p>
                </div>
                <div className="container-editor">
                    <p id="descricao-about" className="element">
                        {
                            event !== undefined ? event.template.description: ''
                        }
                    </p>
                </div>
            </div>

            <SectionCasal tipoFesta={event !== undefined ? event.template.thematics[0].thematic_id: ''} /> 

            <SectionPadrinhos tipoFesta={event !== undefined ? event.template.thematics[0].thematic_id: ''} /> 

            <div className="theme-site-address" id="localizacao">
                <div className="container-editor">
                    <p id="titulo-address" 
                        className={
                            event !== undefined ? 
                            sub_session_items.header.cta_presentear.italico + ' ' + 
                            ' element title-content' : 'element title-content'
                        }
                    >
                        Local da Festa
                    </p>
                </div>
                <div className="container-editor">
                    <p id="descricao-address" className="element" >A festa vai acontecer no dia {event !== undefined ? event.date:''} às {event !== undefined ? event.hour:''} <br /> Quer saber onde? Confere aí!</p>
                </div>
                <div className="container-editor">
                    <p id="endereco-address" className="element"><span>Casa de Festas Casamentos Perfeitos</span><span>{event !== undefined ? event.street:''}, 259, {event !== undefined ? event.neighborhood:''}, {event !== undefined ? event.city:''} - {event !== undefined ? event.state:''}</span></p>
                </div>
                <div className="container-editor">
                    <div id="mapa-address" className="element">
                        <iframe src={"https://maps.google.com/maps?q=" + street + '&t=&z=17&ie=UTF8&iwloc=&output=embed'} width="100%" height="450" frameBorder="0" title="localização da festa" allowFullScreen></iframe>
                    </div>
                </div>
            </div>

            <div className="theme-site-album" id="fotos">
                <div className="container-editor">
                    <p id="titulo-album" className="element title-content">Fotos do José</p>
                </div>
                <div className="container-editor">
                    <div id="grid-album" className="element">
                        <div className="photo-album"></div>
                        <div className="photo-album"></div>
                        <div className="photo-album"></div>
                        <div className="photo-album"></div>
                        <div className="photo-album"></div>
                        <div className="photo-album"></div>
                    </div>
                </div>
            </div>
            <footer className="theme-site-countdown" 
                style={{  
                    background: event !== undefined ? sub_session_items.rodape.background.fundo_cor : ''
                }}
            >
                <div className="container-editor">
                    <p id="titulo-countdown" className="element">Não se esqueça...</p>
                </div>
                <div className="container-editor">
                    <p id="descricao-countdown" className="element title-content">Faltam apenas 00 dias para a festa!</p>
                </div>
                <div className="container-editor">
                    <Link to={event_gifts_link} id="cta-primary-countdown" className="btn-cta btn-cta-primary">Presentear aniversariante</Link>
                </div>
                <div className="container-editor">
                    <p id="cta-secondary-countdown"  className="btn-cta btn-cta-secondary" data-modal="confirmar-presenca">Confirmar presença</p>
                </div>
            </footer>
        </div>
      )
    }
}

const mapStateToProps = state => ({
    themeConfigurations: state.themeConfigurations.items,
    event: state.getEvent.event
})


export default connect(mapStateToProps, {fetchGetEvent, fetchThemeConfigurations})(HomeConvidado);
