var selectStatus = $(".custom-select-editor:not(.text-style)").select2({
  tags: false,
  minimumResultsForSearch: -1,
  templateSelection: formatStatus,
  templateResult: formatStatus
});

function formatStatus(status) {
  var $state = $('<span  style="font-family:' + status.id + ';">' + status.text + '</span>');
  return $state;
}

;

function rgb2hex(rgb) {
  rgb = rgb.match(/^rgb\((\d+),\s*(\d+),\s*(\d+)\)$/);

  function hex(x) {
    return ("0" + parseInt(x).toString(16)).slice(-2);
  }

  return "#" + hex(rgb[1]) + hex(rgb[2]) + hex(rgb[3]);
}

function colorLum(color) {
  let rgb = color.match(/\d+/g);
  let vermelho = rgb[0];
  let verde = rgb[1];
  let azul = rgb[2];
  let lum = (vermelho * 299 + verde * 587 + azul * 114) / 1000;

  if (lum < 128) {
    return "white";
  } else {
    return "black";
  }
}

