import axios from "axios";


export const AUTHENTICATED = "authenticated_user";
export const UNAUTHENTICATED = "unauthenticated_user";
export const AUTHENTICATION_ERROR = "authentication_error";

export const FETCH_CONTACT_BEGIN   = 'FETCH_CONTACT_BEGIN';
export const FETCH_CONTACT_SUCCESS = 'FETCH_CONTACT_SUCCESS';
export const FETCH_CONTACT_FAILURE = 'FETCH_CONTACT_FAILURE';

export const FETCH_EVENTS_BEGIN   = 'FETCH_EVENTS_BEGIN';
export const FETCH_EVENTS_FAILURE = 'FETCH_EVENTS_FAILURE';
export const FETCH_EVENTS_SUCCESS = 'FETCH_EVENTS_SUCCESS';

export const FETCH_HOME_BEGIN   = 'FETCH_HOME_BEGIN';
export const FETCH_HOME_FAILURE = 'FETCH_HOME_FAILURE';
export const FETCH_HOME_SUCCESS = 'FETCH_HOME_SUCCESS';

export const FETCH_PAGE_BEGIN   = 'FETCH_PAGE_BEGIN';
export const FETCH_PAGE_FAILURE = 'FETCH_PAGE_FAILURE';
export const FETCH_PAGE_SUCCESS = 'FETCH_PAGE_SUCCESS';

export const FETCH_TEMPLATE_BEGIN   = 'FETCH_TEMPLATE_BEGIN';
export const FETCH_TEMPLATE_FAILURE = 'FETCH_TEMPLATE_FAILURE';
export const FETCH_TEMPLATE_SUCCESS = 'FETCH_TEMPLATE_SUCCESS';

export const FETCH_ABOUT_BEGIN   = 'FETCH_ABOUT_BEGIN';
export const FETCH_ABOUT_FAILURE = 'FETCH_ABOUT_FAILURE';
export const FETCH_ABOUT_SUCCESS = 'FETCH_ABOUT_SUCCESS';

const URL   = "http://51.15.99.120:4700";
const urlWP = "http://www.localkindle.com.br/netgift_api/wp-json/ntgift/api";

const config = {
  headers: {"Access-Control-Allow-Origin": "*"},
}

export function signInAction({ email, password }, history) {
  return async dispatch => {
    try {
      const res = await axios.post(`${URL}/login`, { "email" : email, "password" : password, "facebook_id" : "" }, config);
      dispatch({ type: AUTHENTICATED });
      var jsonAux = JSON.stringify(res.data);
      localStorage.setItem("user", jsonAux);
      history.push("/dashboard");
    } catch (error) {
      dispatch({
        type: AUTHENTICATION_ERROR,
        payload: "Invalid email or password"
      });
    }
  };
}

export const POSTEDTHEME = "postedtheme";
export const UNPOSTEDTHEME = "unpostedtheme";
export const POSTEDTHEMEERROR = "postederror";


export function signOutAction(history) {
  localStorage.clear();
  window.location = "/login";
  return {
    type: UNAUTHENTICATED
  };
}

export const fetchThemesBegin = () => ({
    type: FETCH_THEMES_BEGIN
  });

export const fetchThemesSuccess = themes => ({
    type: FETCH_THEMES_SUCCESS,
    payload: { themes }
});

export const fetchThemesFailure = error => ({
    type: FETCH_THEMES_FAILURE,
    payload: { error }
});

export function fetchThemes(){
    return dispatch => {
      dispatch(fetchThemesBegin());
      return axios.get(`${URL}/themes`)
        .then((response) => {
          var {data} = response;
          dispatch(fetchThemesSuccess(data));
          return data;
        })
        .catch(error => dispatch(fetchThemesFailure(error)));
    }
}

export function fetchTheme(id){
  return dispatch => {
    dispatch(fetchThemesBegin());
    return axios.get(`${URL}/themes/` + id)
      .then((response) => {
        var {data} = response;
        dispatch(fetchThemesSuccess(data));
        return data;
      })
      .catch(error => dispatch(fetchThemesFailure(error)));
  }
}



export const fetchEventsBegin = () => ({
    type: FETCH_EVENTS_BEGIN
});

export const fetchEventsSuccess = events => ({
    type: FETCH_EVENTS_SUCCESS,
    payload: { events }
});

export const fetchEventsFailure = error => ({
    type: FETCH_EVENTS_FAILURE,
    payload: { error }
});

export function fetchEvents(){
  return dispatch => {
    dispatch(fetchEventsBegin());
    return axios.get(`${URL}/events/all`)
      .then((response) => {
        var {data} = response;
        dispatch(fetchEventsSuccess(data));
        return data;
      })
      .catch(error => dispatch(fetchEventsFailure(error)));
  }
}

export const fetchHomeBegin = () => ({
    type: FETCH_HOME_BEGIN
});

const fetchPageBegin = type_fetch => ({
  type: type_fetch
});

const fetchPageSuccess = (type_fetch, page) => ({
  type: type_fetch,
  payload: { page }
});

const fetchPageFailure = (type_fetch, type_playload) => ({
  type: type_fetch,
  payload: { type_playload }
});

export function fetchPage(post_id, post_types){
  return dispatch => {
    dispatch(fetchPageBegin(FETCH_PAGE_BEGIN));
    return axios.get(`${urlWP}/get_page_content?post_id=${post_id}&post_types_listed=${post_types}`)
      .then((response) => {
        var {data} = response;
        dispatch(fetchPageSuccess(FETCH_PAGE_SUCCESS, data));
        return data;
      })
      .catch(error => dispatch(fetchPageFailure(FETCH_PAGE_FAILURE, error)));
  }
}

export const fetchHomeSuccess = home => ({
    type: FETCH_HOME_SUCCESS,
    payload: { home }
});

export const fetchHomeFailure = error => ({
    type: FETCH_HOME_FAILURE,
    payload: { error }
});

export function fetchHome(){
  return dispatch => {
    dispatch(fetchHomeBegin());
    return axios.get(`${urlWP}/get_home_content`)
      .then((response) => {
        var {data} = response;

        dispatch(fetchHomeSuccess(data));
        return data;
      })
      .catch(error => dispatch(fetchHomeFailure(error)));
  }
}

export const fetchAboutBegin = () => ({
    type: FETCH_ABOUT_BEGIN
});

export const fetchAboutSuccess = about => ({
    type: FETCH_ABOUT_SUCCESS,
    payload: { about }
});

export const fetchAboutFailure = error => ({
    type: FETCH_ABOUT_FAILURE,
    payload: { error }
});

export function fetchAbout(){
  return dispatch => {
    dispatch(fetchAboutBegin());
    return axios.get(`${urlWP}/get_page_content?post_id=120`)
      .then((response) => {
        var {data} = response;

        dispatch(fetchAboutSuccess(data));
        return data;
      })
      .catch(error => dispatch(fetchAboutFailure(error)));
  }
}

function handleErrors(response) {
  if (!response.ok) {
    throw Error(response.statusText);
  }
  return response;
}
