import{
    SET_NOTIFICACAO
}
from '../actions/notificarActions.js';

const initialState = {
    items: [],
    loading: false,
    error: null
  };

  export default function notificarReducer(state = initialState, action) {
    switch(action.type) {
        case SET_NOTIFICACAO:
            return {
                ...state,
                loading: false,
                loadingNext: false,
                items: action.payload.themes
            };
    }
  }
