import {
    FETCH_GIFTS_BEGIN,
    FETCH_GIFTS_FAILURE,
    FETCH_GIFTS_SUCCESS,
} from '../actions';

import { FETCH_GIFTS_CART_SUCCESS } from '../actions/carrinhoAction';

  const initialState = {
    gifts: [],
    loading: true,
    error: null
  };

  export default function productReducer(state = initialState, action) {

    switch(action.type) {
      case FETCH_GIFTS_BEGIN:
        // Mark the state as "loading" so we can show a spinner or something
        // Also, reset any errors. We're starting fresh.
        return {
          ...state,
          loading: true,
          error: null
        };

      case FETCH_GIFTS_FAILURE:
        // All done: set loading "false".
        // Also, replace the items with the ones from the server

        return {
            ...state,
            loading: false,
            error: action.payload.error,
            gifts: []
          };

      case FETCH_GIFTS_SUCCESS:
        // The request failed. It's done. So set loading to "false".
        // Save the error, so we can display it somewhere.
        // Since it failed, we don't have items to display anymore, so set `items` empty.
        //
        // This is all up to you and your app though:
        // maybe you want to keep the items around!
        // Do whatever seems right for your use case.
        return {
            ...state,
            loading: false,
            gifts: action.payload.gifts
          };

      case FETCH_GIFTS_CART_SUCCESS: 

          return{
            ...state,
            giftsOnCart: action.payload.giftsOnCart
          }
      default:
        // ALWAYS have a default case in a reducer
        return state;
    }
  }
