import React, { Component } from "react";
// import Select from 'react-select';
import $ from "jquery";
import { connect } from "react-redux";
import { fetchGift } from "../actions/giftActions";
import setSelect2 from "../assets/js/setSelect2";

// const options = [
//     { value: '1', label: 'Categoria 1' },
//     { value: '2', label: 'Categoria 2' },
//     { value: '3', label: 'Categoria 3' },
//   ];

const bgImage = {
    "backgroundImage": "url(https://havan.vteximg.com.br/arquivos/ids/3270937-1200-1200/aparelho-de-jantar-e-cha-rosas-20-pecas-biona-1-21.jpg?v=636669152638700000)"
  }


class PresentesAdminEditar extends Component {

    constructor(props) {
        super(props);
         // this.state = { selectedOption: null, };
         // this.handleChange = this.handleChange.bind(this);
    }

    // handleChange = selectedOption => {
    //     this.setState({ selectedOption });
    //     console.log(`Option selected:`, selectedOption);
    //   };

  componentDidMount(){
    const id = this.props.match.params.id;
    this.props.dispatch(fetchGift(id));
  }

  render() {
      const {gifts} = this.props;
      const {items, error} = gifts;
      const item = items[0];
      // const { selectedOption } = this.state;
      console.log(item)

    return (
        <div className="wrap-content novo-tema presentes-2">

        <div className="content-title">
            <div className="flex flex-baseline flex-space">
                <div>
                    <h1>Presentes</h1>
                    <h2>Edite, exclua ou crie novos presentes</h2>
                </div>

                <button className="gradient fullcolor disabled">Novo presente</button>
            </div>
        </div>

        <section className="card-options">
            <div className="content-card-options grid">
                <div>
                    <form>
                        <div className="header-card-options">
                            <h2 className="title">Editar presente</h2>
                            <p className="subtitle">Lorem ipsum dolor sit amet, consectetur adipisicing elit. Ex quas cum reprehenderit fuga repellat, maiores ut minus accusantium</p>
                        </div>
                        <div className="line"></div>
                        <div className="main-options">
                            <div className="grid grid-input">

                                <div className="">
                                    <label htmlFor="tipo-festa" className="label-theme">Nome do presente <i className="ng-info-circled"></i></label>

                                    <input type="text" className="input-theme" name="nome-presente" id="nome-presente" placeholder="Digite o nome do presente" value={item ? item.description : ""} />
                                </div>

                                <div>
                                    <label htmlFor="valor-presente" className="label-theme">Valor do presente</label>

                                    <input type="text" className="input-theme" name="valor-presente" id="valor-presente" placeholder="Ex: R$350,00" value={item ? item.price : ""} />
                                </div>

                                <div>
                                    <label htmlFor="tipo-festa" className="label-theme">Categoria do presente</label>

                                    <div className="selecione flex flex-space flex-center">
                                        <span>Selecione a categoria</span>

                                        <i className="ng-down-open-big"></i>

                                        {/* <Select
                                            value={selectedOption}
                                            onChange={this.handleChange}
                                            options={options}
                                            isMulti ={true}
                                            className={"bg-white categoria-tema"}
                                            placeholder={"Selecione a Categoria"}
                                        />*/}

                                    </div>

                                     <div className="wrap-categoria-tema">
                                        <div className="main-categorias">
                                            <div className="triangle"></div>
                                            <div className="item-categorias">
                                                <p className="title-categorias">Categorias atribuídas <i className="ng-info-circled"></i></p>
                                                <div className="tags-categorias"></div>
                                                <div className="line"></div>
                                            </div>

                                            <div className="select-categoria-tema">
                                                <select className="custom-select bg-white categoria-tema" data-select multiple >
                                                    <option value="1">Categoria 1</option>
                                                    <option value="2">Categoria 2</option>
                                                    <option value="3">Categoria 3</option>
                                                    <option value="4">Categoria 4</option>
                                                    <option value="5">Categoria 5</option>
                                                </select>
                                                <div className="dropdown-categoria-tema"></div>
                                            </div>
                                        </div>
                                    </div>
                                </div>
                            </div>

                            <div className="div-textarea">
                                <label htmlFor="descricao-presente" className="label-theme">Descrição do presente</label>

                                <textarea name="" id="descricao_presente" cols="30" rows="5" placeholder="Digite a descrição que aparecerá para o usuário" value={item ? item.description : ""}></textarea>
                            </div>
                        </div>

                        <div className="foto-presente">
                            <div className="add-foto">
                                <button className="fileup-btn">
                                    Adicionar foto
                                    <input type="file" id="upload-presente" multiple accept="image/*" />
                                </button>
                                <div id="upload-presente-preview" className="queue"></div>
                            </div>

                            <button className="fullcolor gradient">Salvar e criar presente</button>
                        </div>
                    </form>
                </div>

                <div className="card-gift  flex flex-column flex-space">
                    <div className="card-gift-thumb" style={bgImage}></div>
                    <div className="flex flex-column">
                        <div className="card-gift-name">Aparelho de jantar</div>
                        <div className="card-gift-price">R$3.343,60</div>
                        <div className="card-gift-desc">Mauris sit amet pulvinar purus, id efficitur tellus. Curabitur facilisis enim quis nisl aliqua</div>
                    </div>
                </div>
            </div>
        </section>
    </div>
    );
  }
}


const mapStateToProps = state => ({
    gifts: state.gifts
});

export default connect(mapStateToProps)(PresentesAdminEditar);
