import React, { Component } from "react";
import { connect } from "react-redux";
import $ from 'jquery';

import TemaComponent from "./TemaComponent";
import {fetchThemes} from "../actions/themesActions";
import {fetchGetEvent} from "../actions/getEventAction";

import Select from 'react-select';

class SiteFesta extends Component {
    componentDidMount(){
        this.props.fetchThemes( null, null, null, null, null, 1);
        this.props.fetchGetEvent(this.props.match.params.id)
    }

    state = {
        header: {
            titulo: {
                features: {
                    negrito: null,
                    italico: null,
                    sublinhado: null,
                    alinhamento: null,
                    estilo: null,
                    texto_fonte_tamanho: null,
                    texto_fonte_cor: null,
                    texto_fundo_cor: null,
                    font: null
                }
            }
        },
    }

    handleChange = (newValue: any, actionMeta: any ) => {
        newValue.object.sessions.map((session) => {
            console.log(session)
            let sessionName = session.session;
            session.sub_sessions.map((sub) => {
                console.log(sub)
                let subName = sub.sub_session;
                sub.features.map((fet) => {
                    let fetName = fet.name;
                    let fetValue = fet.value;
                    this.setState({
                        sessionName : {
                            subName : {
                                features : {
                                    fetName : fetValue
                                }
                            }
                        }
                    })
                })
            })
        })
        this.setState({
            headerBackgroundImage : newValue.object.sessions[0].sub_sessions[5].features[0].value
        })
	}

    render() {
        const {items} = this.props.themes;
        const {EVENTO} = this.props.event.items

		var categories_options = [];

		if ( undefined !== items ) {
            items.map( ( theme ) => {
                    var id = theme.id;
                    var name = theme.name;
                    categories_options.push( { value: id, label : name, object : theme } );
				})
		}
        return(
            <div className="site-aniversario">
                <div className="container">
                    <div className="summary without-divider">
                        <div className="left">
                            <h2 className="title">Site da Festa</h2>
                            <p className="subtitle">Fusce dui mauris, efficitur non auctor in, ornare id erat.</p>
                        </div>
                    </div>

                    <div className="flex flex-space my50">

                        <div className="config">
                            <div className="config-accordion without-toggle">

                                <div className="config-item">

                                    <div className="config-item-title flex flex-center flex-space">
                                        <p>Tema</p>
                                        <i className="ng-down-open"></i>
                                    </div>
                                    <div className="config-item-content tema">
                                        <div className="divider"></div>
                                        <p className="blue-small">Você está usando</p>
                                        {/* <p className="title">{EVENTO ? EVENTO.theme : "--"}</p> */}
                                        <div className="flex flex-space flex-end">
                                            <div className="flex flex-column w56p">
                                                <label htmlFor="alterar-tema" className="blue-small">Alterar o tema</label>

                                                <Select
                                                    onChange={this.handleChange}
                                                    options={categories_options}
                                                    className="selecione"
                                                    closeMenuOnSelect={false}
                                                    id={"alterar-tema"}
                                                    placeholder="Selecione"
                                                    noOptionsMessage={() => { return "Tipo não encontrado." }}
                                                    isMulti={false}
